import { useState, useEffect } from "react";
import { addTimesheet, getTimesheets } from "../api";

const EmployeeDashboard = () => {
    const [timesheets, setTimesheets] = useState([]);
    const [form, setForm] = useState({ project: "", hours: "", description: "", date: "" });
    const [message, setMessage] = useState("");

    useEffect(() => {
        fetchTimesheets();
    }, []);

    const fetchTimesheets = async () => {
        const data = await getTimesheets();
        setTimesheets(data);
    };

    const handleAddTimesheet = async (e) => {
        e.preventDefault();
        const data = await addTimesheet(form.project, parseInt(form.hours), form.description, form.date);
        setMessage(data.message);
        if (data.message === "Timesheet başarıyla eklendi") {
            fetchTimesheets();
        }
    };

    return (
        <div>
            <h2>Çalışan Dashboard</h2>
            <h3>Timesheet Ekle</h3>
            <form onSubmit={handleAddTimesheet}>
                <select value={form.project} onChange={(e) => setForm({ ...form, project: e.target.value })} required>
                    <option value="">Proje Seçin</option>
                    <option value="Firma A">Firma A</option>
                    <option value="Firma B">Firma B</option>
                    <option value="Firma C">Firma C</option>
                    <option value="Internal">Internal</option>
                    <option value="Resmî Tatil">Resmî Tatil</option>
                    <option value="İzin">İzin</option>
                </select>
                <input type="number" min="1" max="8" placeholder="Mesai (Saat)" required
                    value={form.hours} onChange={(e) => setForm({ ...form, hours: e.target.value })} />
                <input type="text" placeholder="Açıklama" value={form.description}
                    onChange={(e) => setForm({ ...form, description: e.target.value })} />
                <input type="date" required value={form.date}
                    onChange={(e) => setForm({ ...form, date: e.target.value })} />
                <button type="submit">Ekle</button>
            </form>
            <p>{message}</p>

            <h3>Timesheet Listesi</h3>
            <ul>
                {timesheets.map((t) => (
                    <li key={t.id}>
                        {t.date} - {t.project}: {t.hours} saat - {t.description}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default EmployeeDashboard;
