import { useState } from "react";
import { addTimesheet } from "../api"; // ✅ Import this

const TimesheetForm = ({ onSuccess, initialData = {}, onSubmit }) => {
    const [project, setProject] = useState(initialData.project || "Firma A");
    const [hours, setHours] = useState(initialData.hours || 1);
    const [description, setDescription] = useState(initialData.description || "");
    const [date, setDate] = useState(initialData.date || "");

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (onSubmit) {
            onSubmit({ project, hours, description, date }); // ✅ For editing an existing timesheet
        } else {
            // ✅ For adding a new timesheet
            const response = await addTimesheet(project, hours, description, date);
            if (response.message) {
                alert(response.message);
                onSuccess();
            }
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <select value={project} onChange={(e) => setProject(e.target.value)}>
                <option value="Firma A">Firma A</option>
                <option value="Firma B">Firma B</option>
                <option value="Firma C">Firma C</option>
                <option value="Internal">Internal</option>
                <option value="Resmî Tatil">Resmî Tatil</option>
                <option value="İzin">İzin</option>
            </select>
            <input type="number" min="1" max="8" value={hours} onChange={(e) => setHours(e.target.value)} required />
            <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
            <input type="text" placeholder="Açıklama" value={description} onChange={(e) => setDescription(e.target.value)} />
            <button type="submit">{initialData.project ? "Güncelle" : "Ekle"}</button>
        </form>
    );
};

export default TimesheetForm;
